import './App.css';
import {useState} from "react"
import Axios from "axios"
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {

  const [username,setUsername]=useState("");
  const [password,setPassword]=useState("");

  const [usuariosList,setUsuarios] = useState([]);

  const add = () => {
    Axios.post("http://localhost:3001/create",{
      username:username,
      password:password
    }).then(()=>{
      alert("user added")});
  }

  const getUsuarios = () => {
    Axios.get("http://localhost:3001/usuarios").then((response)=>{
      setUsuarios(response.data);
    });
  }
  //getUsuarios();

  return (
    <div className="container">
    <div className="App">
      <div className='lista'>




      {
        usuariosList.map((val,key)=>{
          return <div className=''>{val.username}</div>
        })


      }
      </div>
    </div>

        <div className="card text-center">
      <div className="card-header">
        Examen Final
      </div>
      <div className="card-body">
        <div className="input-group mb-3">
        <span className="input-group-text" id="basic-addon1">Username:</span>
        <input onChange={(event) => {
          setUsername(event.target.value);
        }} type="text" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"></input>
        </div>
        <div className="input-group mb-3">
        <span className="input-group-text" id="basic-addon1">Password:</span>
        <input onChange={(event) => {
          setUsername(event.target.value);
        }} type="password" className="form-control" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"></input>
        </div>
      </div>
      <div className="card-footer text-muted">
      <button className='btn btn-success' onClick={add}>Registrar</button>
      </div>
    </div>
    <table class="table table-striped">
        <thead>
        <tr>
          <th scope="col">Username</th>
          <th scope="col">Password</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <th scope="row">1</th>
          <td>Mark</td>
        </tr>
      </tbody>
    </table>
    </div>
  );
}

export default App;
